mapper.select:
SELECT ifnull(( SELECT SUM( a.apply_amount * b.tax_unit_price ) FROM tb_material_apply a LEFT JOIN tb_components_parts b ON a.parts_id = b.id LEFT JOIN tb_vehicle v ON a.vehicle_id = v.id WHERE a.parts_type = 2
and date (a.arrival_time) >= date(#{startDate})
and date (a.arrival_time) <= date(#{endDate})
and a.organization_id = #{organizationId}
and a.vehicle_no = #{vehicleNo}
),0) + ifnull(( SELECT SUM( a1.apply_amount * c.tax_unit_price ) FROM tb_material_apply a1 LEFT JOIN tb_consumptive_material c ON a1.parts_id = c.id LEFT JOIN tb_vehicle v1 ON a1.vehicle_id = v1.id WHERE a1.parts_type = 1
and date (a1.arrival_time) >= date(#{startDate})
and date (a1.arrival_time) <= date(#{endDate})
and a1.organization_id = #{organizationId}
and a1.vehicle_no = #{vehicleNo}
), 0) FROM DUAL

