FROM tb_material_apply a LEFT JOIN tb_organization b ON a.organization_id = b.id LEFT JOIN tb_user c ON a.user_id = c.id LEFT JOIN tb_vehicle d ON a.vehicle_id = d.id LEFT JOIN tb_components_parts e ON e.id = a.parts_id WHERE a.parts_type = 2 and a.organization_id = #{organizationId}
